let output = [];

for (item of items) {
  // Skip hxl row.
  if (item.json.ISO3.substr(0, 1) == '#') {
    continue;
  }

  // Skip empty iso3.
  if (item.json.ISO3 == '') {
    continue;
  }

  if (item.json['Conflict Stock Displacement']) {
    output.push({
      id: 'idps_' + item.json.ISO3 + '_' + item.json.Year + '_conflict_stock_displacement',
      country: item.json.Name,
      iso3: item.json.ISO3,
      year: item.json.Year,
      name: 'Conflict Stock Displacement',
      value: item.json['Conflict Stock Displacement'],
      source: 'iDMC',
      url: 'https://data.humdata.org/dataset/idmc-internally-displaced-persons-idps',
      tags: '["idps"]',
    });
  }

  if (item.json['Conflict Internal Displacements']) {
    output.push({
      id: 'idps_' + item.json.ISO3 + '_' + item.json.Year + '_conflict_internal_displacements',
      country: item.json.Name,
      iso3: item.json.ISO3,
      year: item.json.Year,
      name: 'Conflict Internal Displacements',
      value: item.json['Conflict Internal Displacements'],
      source: 'iDMC',
      url: 'https://data.humdata.org/dataset/idmc-internally-displaced-persons-idps',
      tags: '["idps"]',
    });
  }

  if (item.json['Disaster Internal Displacements']) {
    output.push({
      id: 'idps_' + item.json.ISO3 + '_' + item.json.Year + '_disaster_internal_displacements',
      country: item.json.Name,
      iso3: item.json.ISO3,
      year: item.json.Year,
      name: 'Disaster Internal Displacements',
      value: item.json['Disaster Internal Displacements'],
      source: 'iDMC',
      url: 'https://data.humdata.org/dataset/idmc-internally-displaced-persons-idps',
      tags: '["idps"]',
    });
  }

  if (item.json['Disaster Stock Displacement']) {
    output.push({
      id: 'idps_' + item.json.ISO3 + '_' + item.json.Year + '_disaster_stock_displacement',
      country: item.json.Name,
      iso3: item.json.ISO3,
      year: item.json.Year,
      name: 'Disaster Stock Displacement',
      value: item.json['Disaster Stock Displacement'],
      source: 'iDMC',
      url: 'https://data.humdata.org/dataset/idmc-internally-displaced-persons-idps',
      tags: '["idps"]',
    });
  }
}

return output;
